functionBody:
// <<LAND_USE_RESIDENTIAL>>

if(!!<<LAND_USE_MAJOR>>){
	return 3;
}

let hotelLevel = 0;
const hotelUnits = <<UNITS_GUEST>> || 0;
if(hotelUnits >= 150 ){
	hotelLevel = 3;
} else if (hotelUnits >= 50) {
	hotelLevel = 2;
} else if (hotelUnits >= 16) {
	hotelLevel = 1;
}

let residentialLevel = 0;
const residentialUnits = (<<UNITS_HABIT_LT3>> || 0) 
	+ (<<UNITS_HABIT_3>> || 0) 
	+ (<<UNITS_HABIT_GT3>> || 0) 
	+ (<<UNITS_CONDO>> || 0);
if (residentialUnits >= 250){
	residentialLevel = 3;
} else if ( residentialUnits >=  50 ){
	residentialLevel = 2;
} else if (residentialUnits >= 16 ) {
	residentialLevel = 1;
}

if(<<STRATEGY_AFFORDABLE>> === 4 || !!<<AFFORDABLE_HOUSING>>){
	residentialLevel = residentialLevel === 0 ? 0 : 1;
}

let auditoriumLevel = 0;
const auditoriumSeats = (<<SEAT_AUDITORIUM>> || 0) ;

if (auditoriumSeats >= 20000){
	auditoriumLevel = 3;
} else if ( auditoriumSeats >=  9000 ){
	auditoriumLevel = 2;
} 

let auditoriumSFLevel = 0;
const auditoriumSF = (<<SF_AUDITORIUM_NO_SEATS>> || 0) ;

if (auditoriumSF >= 500000){
	auditoriumSFLevel = 3;
} else if ( auditoriumSF >=  250000 ){
	auditoriumSFLevel = 2;
} 

let retailLevel = 0;
const retailSF = (<<SF_RETAIL>> || 0)  + (<<SF_FURNITURE>> || 0)  
+ (<<SF_RESTAURANT>> || 0) + (<<SF_HEALTH_CLUB>> || 0) 
+ (<<SF_RESTAURANT_TAKEOUT>> || 0) + (<<SF_INST_MEDICAL_SVC>> || 0)
+ (<<SF_HOSPITAL>> || 0) + (<<SF_CONVALESCENT>> || 0);

if (retailSF >= 250000){
	retailLevel = 3;
} else if ( retailSF >=  100000){
	retailLevel = 2;
} else if ( retailSF >=  50000){
	retailLevel = 1;
} 

let warehouseLevel = 0;
const warehouseSF = (<<SF_WAREHOUSE>> || 0) ;
if (warehouseSF >= 250000){
	warehouseLevel = 3;
} 

let commercialLevel = 0;
const commercialSF = 
	(<<SF_OFFICE>> || 0)
	+ (<<SF_INST_OTHER>> || 0)
	+ (<<SF_INST_GOV>> || 0);
if (commercialSF >= 100000){
	commercialLevel = 3;
} else if ( commercialSF >=  50000 ){
	commercialLevel = 2;
} else if (commercialSF >= 25000) {
	commercialLevel = 1;
}

let schoolLevel = 0;
const schoolStudents = (<<STUDENTS_ELEMENTARY>> || 0)
	+ (<<STUDENTS_TRADE_SCHOOL>> || 0)
	+ (<<HS_STUDENTS>> || 0);
if (schoolStudents >= 500){
	schoolLevel = 3;
} else if ( schoolStudents >=  250 ){
	schoolLevel = 2;
} else if (schoolStudents >= 100) {
	schoolLevel = 1;
}

return Math.max(residentialLevel, hotelLevel, auditoriumLevel, 
	auditoriumSFLevel, retailLevel, warehouseLevel, 
	commercialLevel, schoolLevel);

switch (<<STRATEGY_MOBILITY_INVESTMENT_2>>)
      {   case 1:    return 4;   
        case 2:    return 8;   
        case 3:    return 12;     
        default:   return 0;  	};

if (!<<PARK_REQUIREMENT>> || <<PROJECT_LEVEL>> === 0) {
	return 0;
 }
 const ratio =  <<CALC_PARK_RATIO>> / 100 - 0.00001;
 const ratioPenalty = Math.min(Math.floor((Math.max(ratio,1.0) - 1.0) * 10) * 2, 10);
 return (10 + <<PROJECT_LEVEL>> * 5) + ratioPenalty;

if (<<UNITS_GUEST>> <= 30){
return <<UNITS_GUEST>>;
} else if (<<UNITS_GUEST>> <= 60 ) {
return 30 + (<<UNITS_GUEST>> - 30) * 0.5;
} else {
return 45 + (<<UNITS_GUEST>> - 60) * 0.3;
}

switch (<<STRATEGY_AFFORDABLE>>){
	case 1:
		return 2;
	case 2:
		return 4;
	case 3:
		return 6;
	case 4:
		return 10;
	default:
		return 0;
};

// <<STRATEGY_BIKE_BONUS>>
return Math.max(0, Math.sign(<<PTS_BIKE_1>>) 
	+ Math.sign(<<PTS_BIKE_2>>) 
	+ Math.sign(<<PTS_BIKE_3>>) 
	+ Math.sign(<<PTS_BIKE_4>>) 
	+ Math.sign(<<PTS_BIKE_5>>)
	-2);


switch (<<STRATEGY_INFO_3>>){
	case 1:
		return 4;
	case 2:
		return 6;
	default:
		return 0;
};

switch (<<STRATEGY_PARKING_3>>){
	case 25:
		return 1;
	case 50:
		return 2;
	case 75:
		return 3;
	case 100:
		return 4;
	default:
		return 0;
};

 
switch (<<STRATEGY_TRANSIT_ACCESS_1>>){
	case 1:
		return 6;
	case 2:
		return 8;
	default:
		return 0;
};
